import React, { Component } from 'react'

class Form extends Component {
  constructor() {
    super();

    this.handleFavStateChange = this.handleFavStateChange.bind(this);

    this.state = {
      posicao: '',
      estadoFavorito: '',
      idade: 0,
      vaiComparecer: false
    };
  };

  handleFavStateChange(event) {
    this.setState({
      estadoFavorito: event.target.value,
    });
  }

  handleAgeChange(event) {
    this.setState({
      idade: event.target.value,
    });
  }

  handlePresenceChange(event) {
    if (event.target.checked) {
      this.setState({
      vaiComparecer: true,
      });
    } else {
      this.setState({
      vaiComparecer: false,
      });
    } 

  }

  render() {
    return (
      <>
        <h1>Form</h1>
        <form>
          <select name="posicao">
            <option value="first">First</option>
            <option value="second">Second</option>
            <option value="third">Third</option>
          </select>
          <input
            type="number"
            name="idade"
            value={this.state.idade} 
            onChange={this.handleAgeChange}
          />
          <input
            type="checkbox"
            name="vaiComparecer"
            value={this.state.vaiComparecer} 
            onChange={this.handlePresenceChange}
          />
          <label>
            Estado favorito: 
              <textarea name="estadoFavorito" value={this.state.estadoFavorito} onChange={this.handleFavStateChange} />
          </label>
        </form>
      </>
    )
  }
}

export default Form;
