import React, { Component } from 'react'

class Form extends Component {
  constructor() {
    super();

    this.handleChange = this.handleChange.bind(this);
    this.fileInput = React.createRef();

    this.state = {
      posicao: '',
      estadoFavorito: '',
      idade: 0,
      vaiComparecer: false,
      arquivo: ''
    };
  };

  handleChange({ target }) {
    const { name } = target;
    const value = target.type === 'checkbox' ? target.checked : (target.name === 'arquivo' ? this.fileInput.current.files[0].name : target.value);
    this.setState({
      [name]: value
    });
  } 



  render() {
    return (
      <>
        <h1>Form</h1>
        <form>
          <select name="posicao" defaultValue="first" onChange={this.handleChange}>
            <option value="first">First</option>
            <option value="second">Second</option>
            <option value="third">Third</option>
          </select>
          <input
            type="number"
            name="idade"
            value={this.state.idade} 
            onChange={this.handleChange}
          />
          <input
            type="checkbox"
            name="vaiComparecer"
            value={this.state.vaiComparecer} 
            onChange={this.handleChange}
          />
          <label>
            Estado favorito: 
              <textarea name="estadoFavorito" value={this.state.estadoFavorito} onChange={this.handleChange} />
          </label>
          <input type="file" ref={this.fileInput} name="arquivo" />
        </form>
      </>
    )
  }
}

export default Form;
