import React, { Component } from "react";

class StateFieldset extends Component {
  constructor() {
    super();

    this.fileInput = React.createRef();
  };
  render() {
    const { estadoFavorito, ref, arquivo, handleChange } = this.props;
    return(
      <fieldset>
        <legend>Estado e arquivo</legend>
        <label>
          Estado favorito:
            <textarea name="estadoFavorito" value={estadoFavorito} onChange={handleChange} />
        </label>
        <input onChange={handleChange} type="file" ref={this.fileInput} value={arquivo} name="arquivo" />
      </fieldset>
    )
  }
}

export default StateFieldset;
