import React, { Component } from 'react'
import PositionFieldset from './components/PositionFieldset';

class Form extends Component {
  constructor() {
    super();

    this.handleChange = this.handleChange.bind(this);
    this.fileInput = React.createRef();

    this.state = {
      posicao: '',
      estadoFavorito: '',
      idade: 0,
      vaiComparecer: false,
      arquivo: ''
    };
  };

  handleChange({ target }) {
    const { name, type } = target;
    const value = type === 'checkbox' ? target.checked : (type === 'file' ? this.fileInput.current.files[0].name : target.value);
    console.log(value);
    this.setState({
      [name]: value
    });
  }  

  render() {
    const { posicao, estadoFavorito, idade, vaiComparecer, arquivo } = this.state;

    return (
      <>
        <h1>Form</h1>
        <form>
          <PositionFieldset 
            posicao={posicao} 
            handleChange={this.handleChange}
          /> 
          <fieldset>
            <legend>Idade e vai?</legend>
            <input
              type="number"
              name="idade"
              value={idade}
              onChange={this.handleChange}
            />
            <input
              type="checkbox"
              name="vaiComparecer"
              value={vaiComparecer}
              onChange={this.handleChange}
            />
          </fieldset>
          <fieldset>
            <legend>Estado e arquivo</legend>
            <label>
              Estado favorito:
                <textarea name="estadoFavorito" value={estadoFavorito} onChange={this.handleChange} />
            </label>
            <input onChange={this.handleChange} type="file" ref={this.fileInput} value={arquivo} name="arquivo" />
          </fieldset>
        </form>
      </>
    )
  }
}

export default Form;
