import React, { Component } from "react";

class AgeFieldset extends Component {
  render() {
    const { idade, vaiComparecer, handleChange } = this.props;
    return(
      <fieldset>
        <legend>Idade e vai?</legend>
        <input
          type="number"
          name="idade"
          value={idade}
          onChange={handleChange}
        />
        <input
          type="checkbox"
          name="vaiComparecer"
          value={vaiComparecer}
          onChange={handleChange}
        />
      </fieldset>
    )
  }
}

export default AgeFieldset;
